% A1X = B1disp(sprintf('\nUsing the 1st transformation method\n'));szA1 = size(A1); % getting size of the matrixszB1 = size(B1);a_mx = zeros(szA1);b_vv = zeros(szB1);for i = 1:szB1(1)  b_vv(i) =  B1(i) / A1(i, i);  a_mx(i, i) = 0;   for j = 1:szA1(1)     if i ~= j         a_mx(i, j) =- A1(i, j) / A1(i, i);     end   endenda_mxb_vvmn = norm(a_mx)me = norm(a_mx, inf)X5 = l3_iter_solver(a_mx, b_vv, 10^-5)disp(sprintf('X5 error is %ld', norm(A * X5-B)));