disp('Initializing A and B matrixes...');format shortA = [4 20 1 0; 16 2 0 -2; -4 0 4 32; 2 0 10 0]B = [24; -13; 0; 7]if (det(A) != 0)  format long e  disp(sprintf('Task 1\n'))    X1 = inv(A)*B;  disp('Solution X1 = inv(A) * B = ')  disp(X1);  disp(sprintf('X1 error is %ld', norm(A * X1-B)));    X2 = A\B;  disp('Solution via Gauss method X2 = A\B = ');  disp(X2);  disp(sprintf('X2 error is %ld', norm(A * X2-B)));    X3 = (A^-1)*B;  disp('Solution X3 = (A^-1) * B = ');  disp(X3);  disp(sprintf('X3 error is %ld', norm(A * X3-B)));    X4 = (B'/A')';  disp("Solution via Gauss method X4 = (B'/A')' = ");  disp(X4);  disp(sprintf('X4 error is %ld', norm(A * X4-B)));
  disp(sprintf('\nTask 2\n'));  format short  disp('Swapping rows in A and B matrixes...');  A1 = A([2 1 4 3], :);  B1 = B([2 1 4 3], :);  l3_iter_method_1;  l3_iter_method_2;else  disp('Error: degenerate matrix, det(A) == 0');end